script:
frappe.ui.form.on("Poultry Items", 
    { 
        item_code: function(frm, cdt, cdn) 
            { 
		var d = locals[cdt][cdn];
                const args = {
							"item_code": d.item_code,
							"posting_date":d.date,
							"warehouse":d.warehouse,
							"company":frm.doc.company,
							"qty":d.qty || 1,
							"allow_zero_valuation":1,
				};
                frappe.call(
                    { 
                        method: "erpnext.stock.utils.get_incoming_rate",
                        args: { 
                            args: args
                        },
                        callback: function(r) 
                            { 
                                if(r.message) 
                                    { 

				                    	d.rate=r.message;
				                    	d.qty=(d.qty)?d.qty:1;
                                        frm.refresh_fields()

                                    } 
                            }
                    });

                    frappe.call(
                    { 
                        method: "livestock.poultry.doctype.poultry_items.poultry_items.get_item_stock_uom",
                        args: { 
                            "item_code":d.item_code
                        },
                        callback: function(r) 
                            { 
                                if(r.message) 
                                    { 
                                        d.uom = r.message; 
					                     frm.refresh_fields()
                                    } 
                            }
                    });
            } 
    }
);



frappe.ui.form.on("Project", "before_save", function(frm, cdt, cdn) {
var item = locals[cdt][cdn];

$.each (item.items, function(i, d){

			d.project=frm.doc.project_name;
		});
});
